params.text:
{% set unique_ids = [] %}
{% set result = [] %}
{% for agent in CTX.AgentOrgs %}
    {% if agent.PartitionKey not in unique_ids %}
        {% do unique_ids.append(agent.PartitionKey) %}
        {% set org_item = {'OrgId': agent.PartitionKey} %}
        {% if agent.PartitionKey == CTX.organization.id %}
            {% set org_item = org_item.update({'OrgName': CTX.organization.name}) or org_item %}
        {% else %}
            {% for org in CTX.RewstOrgs %}
                {% if org.id == agent.PartitionKey or org.managing_org_id == agent.PartitionKey %}
                    {% set org_item = org_item.update({'OrgName': org.name}) or org_item %}
                {% endif %}
            {% endfor %}
        {% endif %}
        {% do result.append(org_item) %}
    {% endif %}
{% endfor %}
{{ result }}
